mapper.update:

UPDATE 
	goods_review
SET 
	 user_id = #{userId}
	,goods_code = #{goodsCode}
	,review_star_count = #{reviewStarCount}
	,review_content = #{reviewContent}
	
		,review_image_name = #{reviewImageName}
		,review_image_path = #{reviewImagePath}
	
	,review_reg_date = #{reviewRegDate}
	,review_point_save = #{reviewPointSave}
	,review_point_group = #{reviewPointGroup}
	,review_delete_state = #{reviewDeleteState}
WHERE
	review_code = #{reviewCode};

mapper.insert:
		
		INSERT INTO goods_review(
			 review_code
			,user_id
			,goods_code
			,review_star_count
			,review_content
			
				,review_image_name
				,review_image_path
			
			,review_reg_date
			,review_point_save
			,review_point_group
			,review_delete_state
		)VALUES(
			 sf_new_goods_review_code()
			,#{userId}
			,#{goodsCode}
			,#{reviewStarCount}
			,#{reviewContent}
			
				,#{reviewImageName}
				,#{reviewImagePath}
			
			,#{reviewRegDate}
			,#{reviewPointSave}
			,sf_new_point_group_code(#{reviewPointGroup})
			,'N');
			
            	SELECT review_point_group FROM goods_review ORDER BY review_code DESC LIMIT 0, 1;
