mapper.update:

    	UPDATE 
			payment_complete
		SET 
			 buynow_basket_total_code = #{buynowBasketTotalCode}
			,buynow_basket = #{buynowBasket}
			,user_id = #{userId}
			,final_payment_amount = #{finalPaymentAmount}
			,order_date = #{orderDate}
			,deli_code = NULLIF(#{deliCode}, '') 
			,payment_type_code = #{paymentTypeCode}
			,payment_date = #{paymentDate}
			,point_use_group = NULLIF(#{pointUseGroup}, '') 
			,product_order_state = #{productOrderState}
			,point_save = #{pointSave}
			,product_order_point_group = NULLIF(#{productOrderPointGroup}, '')
			,payment_complete_delete_state = #{paymentCompleteDeleteState}
		WHERE 
			payment_complete_code = #{paymentCompleteCode};

mapper.select:
SELECT buybasket_and_buynow.* FROM (
SELECT bb.basket_group AS 'buynowBasketTotalCode' ,'장바구니' AS 'buynowBasket' ,bb.user_id AS 'userId' ,bb.final_payment_amount AS 'finalPaymentAmount' ,bb.reg_date AS 'orderDate' FROM buybasket AS bb LEFT OUTER JOIN payment_complete AS pc ON bb.basket_group = pc.buynow_basket_total_code WHERE pc.payment_complete_code IS NULL UNION SELECT bn.buynow_code AS 'buynowBasketTotalCode' ,'즉시구매' AS 'buynowBasket' ,bn.user_id AS 'userId' ,bn.final_payment_amount AS 'finalPaymentAmount' ,bn.reg_date AS 'orderDate' FROM buynow AS bn LEFT OUTER JOIN payment_complete AS pc ON bn.buynow_code = pc.buynow_basket_total_code WHERE pc.payment_complete_code IS NULL
)AS buybasket_and_buynow WHERE ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
